$(document).ready(function(){
	$(".menuItemPrincipal").mouseover(menuItemPrincipalHandlerFocus);
	$(".menuItemPrincipal").mouseout(menuItemPrincipalHandlerBlur);
	$(".menuSubItem a").mouseover(menuSubItemHandlerFocus);
	$(".menuSubItem a").mouseout(menuSubItemHandlerBlur);
	$("#divSair").click(btnSairHandlerClick);
	$("#btnNaoConfirmaSaida").click(btnNaoConfirmaSaidaHandlerClick);
	$(".msgFechar input[type='button']").click(msgFecharHandlerClick);
	$("#btnFecharMsgsErro").click(btnFecharMsgsErroHandlerClick);
	$("#msgsErroMinimizada").click(msgsErroMinimizadaHandlerClick);

	$( ".pontoForm" ).draggable();
	apresentarMsg();

});
/****************************************************************************
MENU DO SISTEMA
*****************************************************************************/
function menuItemPrincipalHandlerFocus(eventData){
	$(".menuSubItem").hide();
	$(this).children().show();
	$(this).addClass("menuSelectedItem");
	
}
function menuItemPrincipalHandlerBlur(eventData){
	$(".menuSubItem").hide();
	$(this).removeClass("menuSelectedItem");
}

function menuSubItemHandlerFocus(eventData){
	$(this).addClass("menuSelectedItem");
	
}
function menuSubItemHandlerBlur(eventData){
	$(this).removeClass("menuSelectedItem");
}

/****************************************************************************
BOTÃO SAIR
*****************************************************************************/
function btnSairHandlerClick(eventData){
	$("#pnlConfirmarSaida").show();
}
function btnNaoConfirmaSaidaHandlerClick(eventData){
	$("#pnlConfirmarSaida").hide();
}
/****************************************************************************
MENSAGENS
*****************************************************************************/
function msgFecharHandlerClick(eventData){
	$(".msgsContainer").hide();
}
function btnFecharMsgsErroHandlerClick(eventData){
	$("#msgsErroMinimizada").show();
}
function msgsErroMinimizadaHandlerClick(eventData){
	$("#msgsErro").show();
	$("#msgsErroMinimizada").hide();
}
function apresentarMsg(){
	if($(".itensMsg") == null)
		return;
	
	$(".itensMsg").each(function(){
		if($(this) != null && $(this).html() != null && $(this).html() != ""){
			$(this).html("<ul>" + $(this).html() + "</ul>");
			$(this).parent().show();
		}
	});
}

